   'maths-time-work': [
    { id: 500, question: "A can do a piece of work in 10 days. B can do it in 15 days. In how many days can they do it together?", options: ["6 days", "5 days", "4 days", "8 days"], correctAnswerIndex: 0, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "A एक काम को 10 दिनों में कर सकता है। B उसे 15 दिनों में कर सकता है। वे दोनों मिलकर इसे कितने दिनों में कर सकते हैं?", options: ["6 दिन", "5 दिन", "4 दिन", "8 दिन"] } },
    { id: 501, question: "A and B together can do a piece of work in 12 days, while B alone can finish it in 30 days. A alone can finish the work in:", options: ["18 days", "20 days", "24 days", "25 days"], correctAnswerIndex: 1, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "A और B मिलकर एक काम को 12 दिनों में कर सकते हैं, जबकि B अकेला इसे 30 दिनों में पूरा कर सकता है। A अकेला इस काम को कितने दिनों में पूरा कर सकता है:", options: ["18 दिन", "20 दिन", "24 दिन", "25 दिन"] } },
    { id: 502, question: "A is twice as good a workman as B and together they finish a piece of work in 18 days. In how many days will A alone finish the work?", options: ["27 days", "30 days", "36 days", "54 days"], correctAnswerIndex: 0, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "A, B से दोगुना अच्छा कारीगर है और वे दोनों मिलकर एक काम को 18 दिनों में पूरा करते हैं। A अकेला उस काम को कितने दिनों में पूरा करेगा?", options: ["27 दिन", "30 दिन", "36 दिन", "54 दिन"] } },
    { id: 503, question: "A can do a work in 15 days and B in 20 days. If they work on it together for 4 days, then the fraction of the work that is left is :", options: ["1/4", "1/10", "7/15", "8/15"], correctAnswerIndex: 3, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "A एक काम को 15 दिनों में और B 20 दिनों में कर सकता है। यदि वे 4 दिनों तक एक साथ काम करते हैं, तो काम का कितना अंश बचा है:", options: ["1/4", "1/10", "7/15", "8/15"] } },
    { id: 504, question: "A, B and C can do a piece of work in 20, 30 and 60 days respectively. In how many days can A do the work if he is assisted by B and C on every third day?", options: ["10 days", "12 days", "15 days", "20 days"], correctAnswerIndex: 2, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "A, B और C एक काम को क्रमशः 20, 30 और 60 दिनों में कर सकते हैं। A कितने दिनों में काम कर सकता है यदि उसे हर तीसरे दिन B और C द्वारा सहायता प्रदान की जाती है?", options: ["10 दिन", "12 दिन", "15 दिन", "20 दिन"] } },
    { id: 505, question: "Two pipes A and B can fill a tank in 20 and 30 minutes respectively. If both the pipes are used together, then how long will it take to fill the tank?", options: ["12 min", "15 min", "25 min", "50 min"], correctAnswerIndex: 0, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "दो पाइप A और B एक टंकी को क्रमशः 20 और 30 मिनट में भर सकते हैं। यदि दोनों पाइपों का एक साथ उपयोग किया जाता है, तो टंकी को भरने में कितना समय लगेगा?", options: ["12 मिनट", "15 मिनट", "25 मिनट", "50 मिनट"] } },
    { id: 506, question: "A tap can fill a tank in 6 hours. After half the tank is filled, three more similar taps are opened. What is the total time taken to fill the tank completely?", options: ["3 hrs 15 min", "3 hrs 45 min", "4 hrs", "4 hrs 15 min"], correctAnswerIndex: 1, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "एक नल एक टंकी को 6 घंटे में भर सकता है। आधी टंकी भर जाने के बाद, तीन और समान नल खोल दिए जाते हैं। टंकी को पूरी तरह से भरने में कुल कितना समय लगता है?", options: ["3 घंटे 15 मिनट", "3 घंटे 45 मिनट", "4 घंटे", "4 घंटे 15 मिनट"] } },
    { id: 507, question: "12 men can complete a work in 18 days. Six days after they started working, 4 more men joined them. How many days will all of them take to complete the remaining work?", options: ["10 days", "9 days", "8 days", "12 days"], correctAnswerIndex: 1, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "12 आदमी एक काम को 18 दिनों में पूरा कर सकते हैं। काम शुरू करने के छह दिन बाद, 4 और आदमी उनके साथ जुड़ गए। शेष काम को पूरा करने में उन सभी को कितने दिन लगेंगे?", options: ["10 दिन", "9 दिन", "8 दिन", "12 दिन"] } },
    { id: 508, question: "If 3 men or 6 women can do a piece of work in 16 days, in how many days can 12 men and 8 women do the same piece of work?", options: ["3 days", "4 days", "5 days", "2 days"], correctAnswerIndex: 0, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "यदि 3 पुरुष या 6 महिलाएं एक काम को 16 दिनों में कर सकते हैं, तो 12 पुरुष और 8 महिलाएं उसी काम को कितने दिनों में कर सकते हैं?", options: ["3 दिन", "4 दिन", "5 दिन", "2 दिन"] } },
    { id: 509, question: "A can finish a work in 24 days, B in 9 days and C in 12 days. B and C start the work but are forced to leave after 3 days. The remaining work was done by A in:", options: ["5 days", "6 days", "10 days", "10.5 days"], correctAnswerIndex: 2, subject: 'Maths', exam: 'SSC CGL', chapter: 'Time and Work', translation: { question: "A एक काम को 24 दिनों में, B 9 दिनों में और C 12 दिनों में पूरा कर सकता है। B और C काम शुरू करते हैं लेकिन 3 दिनों के बाद छोड़ने के लिए मजबूर होते हैं। शेष काम A द्वारा कितने दिनों में किया गया:", options: ["5 दिन", "6 दिन", "10 दिन", "10.5 दिन"] } },
  ],
